<template>
  <div class="tableEmpty" v-bind="$attrs">
    <div class="tableEmpty-img">
      <slot name="img" v-if="isImgSlot"></slot>
      <img v-else :src="getProps.img" alt="">
    </div>
    <div class="tableEmpty-text">
      <slot name="text" v-if="isTextSlot"></slot>
      <p v-else>{{getProps.text}}</p>
    </div>
  </div>
</template>

<script lang='ts'>
import { defineComponent, computed } from 'vue'

export default defineComponent({
  props: {
    text: {
      type: String,
      default () {
        return '暂无数据'
      }
    },
    img: {
      type: String,
      default () {
        return 'data:image/png;base64,'
      }
    }
  },
  setup (props, { slots }) {
    const getProps = computed(() => {
      return props
    })
    const isImgSlot = computed(() => {
      return Object.keys(slots).includes('img')
    })
    const isTextSlot = computed(() => {
      return Object.keys(slots).includes('text')
    })
    return {
      getProps,
      isImgSlot,
      isTextSlot
    }
  }
})
</script>
